changeSet.sql:
CREATE TABLE ad_profile ( id bigserial PRIMARY KEY, name character varying(225) NOT NULL );
CREATE TABLE ad_access_profile ( access_id character varying(100) NOT NULL, profile_id bigint NOT NULL, PRIMARY KEY (access_id, profile_id), CONSTRAINT ad_access_profile_profile_id_fkey FOREIGN KEY (profile_id) REFERENCES ad_profile (id) ON UPDATE CASCADE ON DELETE CASCADE );
CREATE TABLE ad_person ( id bigserial PRIMARY KEY, name character varying(100) NOT NULL, address character varying(150), pobox character varying(50), phone character varying(30), email character varying(50) );
CREATE TABLE ad_user ( id bigint PRIMARY KEY, login character varying(25) UNIQUE NOT NULL, password character varying(100) NOT NULL, salt character varying(30) NOT NULL, blocked boolean DEFAULT false NOT NULL, profile_id bigint NOT NULL, CONSTRAINT ad_user_id_fkey FOREIGN KEY (id) REFERENCES ad_person (id) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT ad_user_profile_id_fkey FOREIGN KEY (profile_id) REFERENCES ad_profile (id) ON UPDATE CASCADE ON DELETE CASCADE );
CREATE TABLE ad_event_log ( id bigserial PRIMARY KEY, date timestamp without time zone NOT NULL, message character varying(255) NOT NULL, details character varying(255), level_id character varying(15) NOT NULL, author character varying(25) NOT NULL, ip_address character varying(15) NOT NULL );

